import { Module } from '@nuxt/types';
export declare type ModuleConfig = {
    apiKey: string;
};
export declare type ModuleOptions = {
    fileName?: string;
    mode?: 'all' | 'server' | 'client';
    options: ModuleConfig;
};
export declare type Package = {
    name: string;
    version: string;
    description: string;
    main: string;
    license: string;
};
export interface SpearlyModule<T> extends Module<T> {
    meta: Package;
}
declare const spearlyModule: SpearlyModule<ModuleOptions>;
export default spearlyModule;
