import { Content } from './Content';
export declare type FieldInputType = 'text' | 'number' | 'rich_text' | 'image' | 'calendar' | 'map' | 'content_type' | 'tags';
export declare type FieldType<T> = {
    id: string;
    type: 'field';
    attributes: {
        identifier: string;
        inputType: FieldInputType;
        value: T;
    };
};
export declare type MapValue = {
    preferredFormat: string;
    address: string;
    latitude: number;
    longitude: number;
};
export declare type ContentTypeValue = {
    data: Content;
};
export declare type FieldTypeText = FieldType<string>;
export declare type FieldTypeNumber = FieldType<number>;
export declare type FieldTypeRichText = FieldType<string>;
export declare type FieldTypeImage = FieldType<string>;
export declare type FieldTypeCalendar = FieldType<Date>;
export declare type FieldTypeMap = FieldType<MapValue>;
export declare type FieldTypeTags = FieldType<string[]>;
export declare type FieldTypeContentType = FieldType<ContentTypeValue>;
export declare type FieldTypeAll = FieldTypeText | FieldTypeNumber | FieldTypeRichText | FieldTypeImage | FieldTypeCalendar | FieldTypeMap | FieldTypeTags | FieldTypeContentType;
export declare type ServerFieldTypeCalendar = FieldType<string>;
export declare type ServerFieldTypeAll = FieldTypeText | FieldTypeNumber | FieldTypeRichText | FieldTypeImage | ServerFieldTypeCalendar | FieldTypeMap | FieldTypeTags | FieldTypeContentType;
